sys_script_include.script:
var GetUserAvailableLeaves = Class.create();
GetUserAvailableLeaves.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	GetUserAvailableLeaves: function() {
		var userId = this.getParameter('sysparm_user_id');
		gs.info('eLeave2 - GetUserAvailableLeaves called for user ' + userId);

		var employees = new GlideRecord('x_139317_eleave2_employees');
		employees.addQuery('user', userId);
		employees.query();

		var remainLeaves = 0;
		var employeeFound = false;

		while (employees.next()) { 
			employeeFound = true;
			remainLeaves = employees.available_leaves;
			gs.info('eLeave2 - GetUserAvailableLeaves found employee for user ' + userId);
		}

				var defaultAnualLeaveDays = gs.getProperty('x_139317_eleave2.DefaultAnualLeaveDays');
		gs.info('eLeave2 - GetUserAvailableLeaves: ' + defaultAnualLeaveDays);

		if(!employeeFound)
			{
				var newEmployee = new GlideRecord('x_139317_eleave2_employees');
				newEmployee.initialize();
				newEmployee.user = userId; 
				newEmployee.available_leaves = defaultAnualLeaveDays;	
				//newEmployee.last_update = gs.now();
				newEmployee.insert();

				remainLeaves = defaultAnualLeaveDays;
				gs.info('eLeave2 - GetUserAvailableLeaves not-found employee for user ' + userId);
			}


		return remainLeaves;
	},
    type: 'GetUserAvailableLeaves'
});
